package com.syntax.class03;

public class CompoundOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num=100;
		num=num+10;
		System.out.println(num);
		
		num=num+200;
		System.out.println(num);
		
		int myNumber=100;
		myNumber+=10;
		System.out.println(myNumber);
		
		myNumber+=200;
		System.out.println(myNumber);
		
		myNumber-=100;
		System.out.println(myNumber);
		
		myNumber/=10;
		System.out.println(myNumber);
		
		myNumber*=1000;
		System.out.println(myNumber);
		
		myNumber%=100;
		System.out.println(myNumber);
		
		
		
		
		
		
	}

}
