package com.syntax.class05;

public class IfWithNoBraces {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

boolean isSaturday=true;
if(isSaturday) { 
	System.out.println("Today is Java class");
	System.out.println("And tomorrow i will have a Java class");
	System.out.println("I am a code outside of if statement");
}

//difference between with braces and with no braces

boolean isSunday=false;
if(isSunday)
	System.out.println("Today is Java class");
	System.out.println("And tomorrow i will have a Java ");
	System.out.println("I am a code outside of if ");
}

	}


