package com.syntax.class05;

import java.util.Scanner;

public class Recap {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	Scanner scanner;
	scanner=new Scanner (System.in);
	//I want to Capture Sentence
	System.out.println("Please enter any sentence");
	String sentence=scanner.nextLine();
	System.out.println("You entered sentence"+sentence);
	
	//I want to capture a number
	System.out.println("Pleasee enter any number");
	int number=scanner.nextInt();
	System.out.println("You entered number: "+number);
	
	//I want to capture 1 character
	System.out.println("Plesee eneter any single character");
	
	System.out.println();
	
		
		
	}

}
