package com.syntax.class09;

public class NumPattern {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	     
        for (int i = 1; i <= 7; i++)  {
            for (int j = 1; j <= i; j++) {
                System.out.print(j+" ");
            }
            
            System.out.println();
        }
        System.out.println("----------------------------------------------");
        
        for (int a=1; a<=1; a++) {
			System.out.print(a+" ");
			}
			System.out.println();
			for (int b=1; b<=2; b++) {
				System.out.print(b+" ");
				}
				System.out.println();
				for (int c=1; c<=3; c++) {
					System.out.print(c+" ");
					}
					System.out.println();
					for (int d=1; d<=4; d++) {
						System.out.print(d+" ");
						}
						System.out.println();
						for (int e=1; e<=5; e++) {
							System.out.print(e+" ");
							}
							System.out.println();
							for (int f=1; f<=6; f++) {
								System.out.print(f+" ");
								}
								System.out.println();
								for (int g=1; g<=7; g++) {
									System.out.print(g+" ");
									}
									System.out.println();
			
	}

}
